mapper.select:
SELECT TOP_CONTR_ID, TOP_CONTR_NM, (SELECT CODE_NM FROM CMM_CODE WHERE CODE = TC.TOP_CONTR_DIV) AS TOP_CONTR_DIV, CLIENT_COMP_ID, DATE_FORMAT(TOP_CONTR_DATE, '%Y-%m-%d') AS TOP_CONTR_DATE, DATE_FORMAT(CONTR_ST_DATE, '%Y-%m-%d') AS CONTR_ST_DATE, DATE_FORMAT(CONTR_END_DATE, '%Y-%m-%d') AS CONTR_END_DATE, PROD_NM, FORMAT(CONTR_AMOUNT,',') AS CONTR_AMOUNT, FORMAT(TOTAL_SERV_BOK_AMOUNT, ',') AS TOTAL_SERV_BOK_AMOUNT, DATE_FORMAT(DELIVERY_DEADLINE, '%Y-%m-%d') AS DELIVERY_DEADLINE, DEMAND_INST_ID, REGIST_USER_NAME, REGIST_DATE, DEL_YN, (SELECT COMP_NM FROM COMP WHERE COMP_ID = TC.CLIENT_COMP_ID) AS CLIENT_COMP, (SELECT COMP_NM FROM COMP WHERE COMP_ID = TC.DEMAND_INST_ID) AS DEMAND_INST, TOP_CONTR_FILE_ID FROM TOP_CONTR TC WHERE DEL_YN = 'N'
AND TOP_CONTR_NM LIKE '%' #{topContrNm} '%'
AND TOP_CONTR_DIV = #{topContrDiv}
AND CLIENT_COMP_ID = #{clientCompId}
AND PROD_NM LIKE '%' #{prodNm} '%'
AND DEMAND_INST_ID = #{demandInstId}
AND TOP_CONTR_DATE BETWEEN #{topContrStDate} AND #{topContrEndDate}
ORDER BY REGIST_DATE DESC
